function createBarChart() {
    $("#barChart").kendoChart({
        title: {
            text: "Monthly Case Summary"
        },
        legend: {
            position: "top"
        },
        seriesDefaults: {
            type: "column"
        },
        series: [{
            name: "দাখিল",
            data: [3907, 7943, 7848, 9284, 9263, 9801],
            color: "#9ac70a"
        }, {
            name: "নিষ্পতি",
            data: [4743, 7295, 7175, 6376, 8153, 8535],
            color: "#1A75b6"
        }],
        valueAxis: {
            labels: {
                format: "{0}"
            },
            line: {
                visible: false
            },
            axisCrossingValue: 0
        },
        categoryAxis: {
            categories: ['Oct (22)', 'Nov (22)', 'Dec (22)', 'Jan (23)', 'Feb (23)', 'Mar (23)'],
            line: {
                visible: false
            },
            labels: {

            }
        },
        tooltip: {
            visible: true,
            format: "{0}",
            template: "#= category #: \n #= value#"
        }
    });
}


function createChart() {
    $("#chart").kendoChart({
        title: {
            position: "top",
            text: "Case Priority"
        },
        legend: {
            visible: false
        },
        chartArea: {
            background: ""
        },
        seriesDefaults: {
            labels: {
                visible: true,
                background: "transparent",
                template: "#= category #: \n #= value#%"
            }
        },
        series: [{
            type: "pie",
            startAngle: 150,
            data: [{
                category: "High",
                value: 60,
                color: "#9ac70a"
            }, {
                category: "Normal",
                value: 25,
                color: "#8f8fff"
            }, {
                category: "Low",
                value: 15,
                color: "#1A75b6"
            }]
        }],
        tooltip: {
            visible: true,
            format: "{0}%"
        }
    });
}
