$(document).ready(function () {
    createChart();
    createBarChart();
    $("#ddlMinistryDepartment").kendoComboBox({
        placeholder: 'Select Ministry/Department',
        dataTextField: "name",
        dataValueField: "id",
        dataSource: MinistryDepartmentList,
        filter: "contains",
        index: 0,
        change: onMinistryDepartmentChange
    });
    $("#ddlConOffice").kendoComboBox({
        placeholder: 'Select Office',
        dataTextField: "name",
        dataValueField: "id",
        dataSource: [],
        filter: "contains"
    });
    $("#ddlCaseType").kendoComboBox({
        placeholder: 'All ',
        dataTextField: "name",
        dataValueField: "id",
        dataSource: CaseTypeList,
        filter: "contains"
    });
})

function onMinistryDepartmentChange(e) {
    if (this.dataItem() == undefined) {
        $("#ddlConOffice").data('kendoComboBox').dataSource.data([])
        $("#ddlConOffice").data('kendoComboBox').value('');
        return;
    }
    var id = this.dataItem().id
    var FilterData = _.filter(OfficeList, function (item) { return item.parentid == id });
    $("#ddlConOffice").data('kendoComboBox').dataSource.data([])
    $("#ddlConOffice").data('kendoComboBox').value('')
    $("#ddlConOffice").data('kendoComboBox').dataSource.data(FilterData)
}